@if ($item['submenu'] == [])    @php        $ruta='';            if($item['slug']){                $ruta=route($item['slug']);            }    @endphp    <li class="nav-item">        {{--DIRECTORIOS SIN SUB-DIRECTORIOS--}}        <a href="{{ $ruta }}" class="nav-link">            <i class="{{$item['icono']}}"></i>            <p>                {{$item['name']}}                <i class="right fas fa-angle-left"></i>            </p>        </a>    </li>@else    <li class="nav-item has-treeview" id="dir{{$item['id']}}">        {{--DIRECTORIOS--}}        <a href="#" class="nav-link" id="dir_active{{$item['id']}}">            <i class="{{$item['icono']}}"></i>            <p>                {{$item['name']}}                <i class="right fas fa-angle-left"></i>            </p>        </a>        <ul class="nav nav-treeview">            @foreach ($item['submenu'] as $submenu)                @if ($submenu['submenu'] == [])                    @if (in_array($submenu['id'],$permission))                        @php                            $ruta='';                            $active='';                            $menuOpen='';                                if($submenu['slug']){                                    $ruta=route($submenu['slug']);                                    if(isset($route) && ($submenu['slug']==$route)){                                        // $menuOpen='menu-open';                                        $active='active';                                        $menuOpen=$submenu['parent'];                                    }                                }                        @endphp                        {{--SUB-DIRECTORIOS--}}                        <li class="nav-item">                            <a href="{{ $ruta }}" class="nav-link {{$active}}">                                <i class="far fa-circle nav-icon"></i>                                <p>{{ $submenu['name'] }}</p>                            </a>                        </li>                        @if($menuOpen)                            <script type="application/javascript">$(function () {                                    menuOpen({{$menuOpen}})                                });                            </script>                        @endif                    @endif                @else                    @include('backend.layouts.menu-item', [ 'item' => $submenu ])                @endif            @endforeach        </ul>    </li>@endif<script type="application/javascript">    function menuOpen(id) {        $("#dir"+id).addClass('menu-open');        $("#dir_active"+id).addClass('active');    }</script>